  checkProximity() async {
    setState(() {
      showSpinner = true;
    });
    try {
      var addressCoordinates =
          await Geolocator().placemarkFromAddress(selectedAddress);
      var selectedAddressDistance = await Geolocator().distanceBetween(
          addressCoordinates[0].position.latitude,
          addressCoordinates[0].position.longitude,
          userPos.latitude,
          userPos.longitude);
      if (selectedAddressDistance <= 1000) {
//        cod
