                    List<Widget> addressBubbles = [
                      AddressBubble(
                        address: userAddress.value,
                        numAddress: 1,
                      )
                    ];
                    int count = 2;
                    for (var address in addresses) {
                      final add = address.data['Line 1'] +
                          ", " +
                          address.data['Line 2'] +
                          ", " +
                          address.data['City'] +
                          ", " +
                          address.data['State'];
                      addressBubbles.add(
                        AddressBubble(
                          address: add,
                          numAddress: count,
                          addressId: address.documentID,
                        ),
                      );
                      count++;
                    }
                    return Expanded(
                      child: ListView(
                        children: addressBubbles,
                      ),
                    );
                  },
