class AddressBubble extends StatefulWidget {
  AddressBubble({this.address, this.numAddress, this.addressId});

  final String address;
  final int numAddress;
  final String addressId;

  @override
  _AddressBubbleState createState() => _AddressBubbleState();
}

class _AddressBubbleState extends State<AddressBubble> {
  int addressNumber;
  var _fireStore = Firestore.instance;

  @override
  void initState() {
    // TODO: implement initState
    super.initState();
    setState(() {
      addressNumber = widget.numAddress;
    });
  }

  @override
  Widget build(BuildContext context) {
    return Card(
      shape: RoundedRectangleBorder(
        side: BorderSide(color: Colors.deepOrange[400], width: 1),
        borderRadius: BorderRadius.circular(5),
      ),
      color: Colors.white,
      child: Container(
        padding: EdgeInsets.only(left: 10, right: 10, top: 5, bottom: 2),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: <Widget>[
            Row(
              mainAxisAlignment: MainAxisAlignment.spaceBetween,
              children: <Widget>[
                Flexible(
                  child: ValueListenableBuilder(
                    valueListenable: userAddress,
                    builder: (context, value, child) {
                      return Text(
                        widget.address,
                        softWrap: true,
                        style: GoogleFonts.lato(
                          color: Colors.black54,
                          fontSize: 16,
                          fontStyle: FontStyle.italic,
                        ),
                      );
                    },
                  ),
                ),
                ValueListenableBuilder(
                  valueListenable: selectedAddressNumber,
                  builder: (context, value, child) {
                    return GestureDetector(
                      child: Icon(
                        addressNumber == selectedAddressNumber.value
                            ? Icons.radio_button_checked
                            : Icons.radio_button_unchecked,
                        color: Colors.red[700],
                      ),
                      onTap: () {
                        selectedAddressNumber.value = addressNumber;
                        selectedAddress = widget.address;
                      },
                    );
                  },
                ),
              ],
            ),
            SizedBox(
              height: 2,
            ),
            addressNumber == 1
                ? Container()
                : RaisedButton(
                    onPressed: () {
                      if(selectedAddressNumber.value==widget.numAddress){
                        selectedAddressNumber.value = 1;
                        selectedAddress = userAddress.value;
                      }
                      _fireStore
                          .collection('user_account_details')
                          .document(userUid)
                          .collection('Delivery Addresses')
                          .document(widget.addressId)
                          .delete();
                    },
                    color: Colors.red[700],
                    child: Text(
                      'Remove',
                      style:
                          GoogleFonts.lato(color: Colors.white, fontSize: 14),
                    ),
                  ),
          ],
        ),
      ),
    );
  }
}
